#!/usr/bin/env python3
"""Generate isometric icons using OpenAI's image API."""

import argparse
import base64
import json
import os
import sys
import urllib.request
import urllib.error
import uuid

BASE_PROMPT = """\
Create a single isometric icon with a 30-degree isometric projection, viewed from above at a 3/4 angle facing southwest. Center the object in the frame with generous padding.

Style: Clean, minimal line art with flat shading. Sharp, crisp edges. No gradients, textures, noise, or anti-aliased soft edges. The output must be easily convertible to SVG with automated tracing tools.

Colors (use only these):

- #000000 (black) - outlines and details
- #cdd9ee (light blue) - primary color. main and top surfaces
- #6885a9 (blue-gray) - secondary color, additional surfaces and details
- #f0f7ff (off-white) - top surfaces, highlights, light and reflective surfaces
- #4b6e98 (dark blue) - shadow, depth and shaded surfaces


The object itself must be completely solid and opaque with no transparent or semi-transparent areas within it. Only the background outside the object should be transparent.

Do not include: text, labels, background elements, decorations or multiple objects. The icon must work as part of a consistent icon set.

The given reference image is an example of the style and perspective to be used. The subject of the icon should be different from the reference image, but it should be depicted in a similar style and perspective.

Create an icon depicting the following subject:
"""

REFERENCE_IMAGE_B64 = ''

def load_base_prompt():
    """Return the base prompt for icon generation."""
    return BASE_PROMPT


def generate_image(prompt, api_key):
    """Build JSON payload, call images/edits API, return base64 image data."""
    url = "https://api.openai.com/v1/images/edits"
    image_uri = f"data:image/png;base64,{REFERENCE_IMAGE_B64}"
    payload = {
        "model": "gpt-image-1",
        "prompt": prompt,
        "n": 1,
        "size": "1024x1024",
        "output_format": "png",
        "background": "transparent",
        "images": [{"image_url": image_uri}],
    }

    headers = {
        "Content-Type": "application/json",
        "Authorization": f"Bearer {api_key}",
    }

    data = json.dumps(payload).encode("utf-8")
    req = urllib.request.Request(url, data=data, headers=headers, method="POST")

    try:
        with urllib.request.urlopen(req) as response:
            result = json.loads(response.read().decode("utf-8"))
            return result["data"][0]["b64_json"]
    except urllib.error.HTTPError as e:
        error_body = e.read().decode("utf-8")
        print(f"API error (HTTP {e.code}): {error_body}", file=sys.stderr)
        sys.exit(1)


def save_image(b64_data, output_path):
    """Decode base64 and write PNG to file."""
    image_data = base64.b64decode(b64_data)
    with open(output_path, "wb") as f:
        f.write(image_data)


def convert_to_svg(png_path, svg_path):
    """Convert PNG to SVG using vtracer."""
    import subprocess
    result = subprocess.run(
        [
            "vtracer",
            "--input", png_path,
            "--output", svg_path,
            "--mode", "polygon",          # Sharp edges instead of splines
            "--filter_speckle", "8",      # Remove small artifacts
            "--color_precision", "4",     # Fewer color buckets (5 colors)
            "--corner_threshold", "45",   # Preserve sharp corners
            "--segment_length", "4",      # Shorter segments for precision
            "--splice_threshold", "45",   # Join paths at sharp angles
        ],
        capture_output=True, text=True
    )
    if result.returncode != 0:
        print(f"Warning: vtracer failed: {result.stderr}", file=sys.stderr)
        return False
    return True


def main():
    parser = argparse.ArgumentParser(
        description="Generate isometric icons using OpenAI's image API"
    )
    parser.add_argument("prompt", help="Description of what to create")
    parser.add_argument("basename", nargs="?", default=None, help="Base path for output file (without extension). If omitted, uses a random name.")
    parser.add_argument(
        "-f", "--force", action="store_true", help="Overwrite existing file"
    )
    args = parser.parse_args()

    api_key = os.environ.get("OPENAI_API_KEY")
    if not api_key:
        print("Error: OPENAI_API_KEY environment variable not set", file=sys.stderr)
        sys.exit(1)

    basename = args.basename if args.basename else str(uuid.uuid4())[:8]
    output_path = f"{basename}.png"
    svg_path = f"{basename}.svg"

    if not args.force:
        if os.path.exists(output_path):
            print(
                f"Error: {output_path} already exists. Use -f to overwrite.",
                file=sys.stderr,
            )
            sys.exit(1)
        if os.path.exists(svg_path):
            print(
                f"Error: {svg_path} already exists. Use -f to overwrite.",
                file=sys.stderr,
            )
            sys.exit(1)

    output_dir = os.path.dirname(output_path)
    if output_dir:
        os.makedirs(output_dir, exist_ok=True)

    base_prompt = load_base_prompt()
    combined_prompt = f"{base_prompt} {args.prompt}"

    print(f"Generating image for: {args.prompt}")
    b64_data = generate_image(combined_prompt, api_key)

    save_image(b64_data, output_path)
    print(f"Saved to: {output_path}")

    if convert_to_svg(output_path, svg_path):
        print(f"Saved to: {svg_path}")


if __name__ == "__main__":
    main()
